class Logado extends React.Component{



    render (){

        token();
        userinfo();




        return (
            <div className="Logado-header">
                <h2>Autenticação Realizada!</h2>
                <br></br>


<div>
                <button
                            className="botao"
                             onClick = {Receita}
                            >
                                Receita
                    </button>


                <button
                            className="botao"
                            onClick = {Despesas}
                            >
                                Despesas
                    </button>

                    <button
                            className="botao"

                            >
                                Licitações
                    </button>

                    <button
                            className="botao"

                            >
                                ADM
                    </button>
</div>

                <button
                            className="button-logout"
                             onClick = {logout}
                            >
                                Logout
                            </button>
                <br></br>


                <div >

                </div>
            </div>


        )


    }




}




export default Logado;
